function ImagePopup(props){
    return(
        <div className={`popup popup_photo ${props.card ? 'popup_hidden' : ''}`}>
            <figure className="popup__case">
                <button className="popup__close-button popup__close-button-photo" type="button" onClick={props.onClose}></button>
                <img className="popup__image" src={`${props.card.link}`} alt={props.card.name}/>
                <figcaption className="popup__description">{props.card.name}</figcaption>
            </figure>
        </div>
    )
}

export default ImagePopup;
